import React, { useState } from "react";

const SavingsGoalForm = ({ onSaveGoal }) => {
  const [goalName, setGoalName] = useState("");
  const [goalAmount, setGoalAmount] = useState("");
  const [deadline, setDeadline] = useState("");
  const [error, setError] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!goalName || !goalAmount || !deadline) {
      setError("Todos los campos son obligatorios.");
      return;
    }

    onSaveGoal({ goalName, goalAmount: parseFloat(goalAmount), deadline });
    setGoalName("");
    setGoalAmount("");
    setDeadline("");
    setError("");
  };

  return (
    <form onSubmit={handleSubmit} className="savings-goal-form">
      <h2>Meta de Ahorro</h2>

      {error && <p className="error-message">{error}</p>}

      <div className="form-group">
        <label htmlFor="goalName">Nombre de la Meta:</label>
        <input
          type="text"
          id="goalName"
          value={goalName}
          onChange={(e) => setGoalName(e.target.value)}
          placeholder="Ejemplo: Viaje a París"
        />
      </div>

      <div className="form-group">
        <label htmlFor="goalAmount">Cantidad Objetivo:</label>
        <input
          type="number"
          id="goalAmount"
          value={goalAmount}
          onChange={(e) => setGoalAmount(e.target.value)}
          placeholder="Ejemplo: 1000"
        />
      </div>

      <div className="form-group">
        <label htmlFor="deadline">Fecha Límite:</label>
        <input
          type="date"
          id="deadline"
          value={deadline}
          onChange={(e) => setDeadline(e.target.value)}
        />
      </div>

      <button type="submit" className="save-btn">Guardar Meta</button>
    </form>
  );
};

export default SavingsGoalForm;
