// src/contexts/SyncContext.jsx
import React, { createContext, useContext, useEffect, useState, useCallback } from 'react';
import { expenseSservice, savingsService } from '../services/api';
import { getFromLocalStorage } from '../utils/localStorageUtils';

export const SyncContext = createContext();

export const SyncProvider = ({ children }) => {
  const [isSyncing, setIsSyncing] = useState(false);
  const [syncError, setSyncError] = useState(null);
  const [lastSyncTime, setLastSyncTime] = useState(null);
  const [pendingChanges, setPendingChanges] = useState({
    expenses: 0,
    savings: 0
  });

  // Contar cambios pendientes
  const countPendingChanges = useCallback(() => {
    const pendingExpenses = getFromLocalStorage('pendingExpenses') || [];
    const pendingSavings = getFromLocalStorage('pendingSavings') || [];

    setPendingChanges({
      expenses: pendingExpenses.length,
      savings: pendingSavings.length
    });
  }, []);

  // Sincronizar gastos
  const syncExpenses = async () => {
    const pendingExpenses = getFromLocalStorage('pendingExpenses') || [];
    let syncedCount = 0;

    for (const expense of pendingExpenses) {
      try {
        if (expense.pending) {
          await expenseService.create(expense);
          syncedCount++;
        }
      } catch (error) {
        console.error('Error sincronizando gasto:', error);
        throw new Error('Error al sincronizar gastos');
      }
    }

    return syncedCount;
  };

  // Sincronizar ahorros
  const syncSavings = async () => {
    const pendingSavings = getFromLocalStorage('pendingSavings') || [];
    let syncedCount = 0;

    for (const saving of pendingSavings) {
      try {
        if (saving.pending) {
          await savingsService.create(saving);
          syncedCount++;
        }
      } catch (error) {
        console.error('Error sincronizando ahorro:', error);
        throw new Error('Error al sincronizar ahorros');
      }
    }

    return syncedCount;
  };

  // Función principal de sincronización
  const syncAll = useCallback(async () => {
    if (isSyncing || !navigator.onLine) return;

    setIsSyncing(true);
    setSyncError(null);

    try {
      // Sincronizar todo
      const [expenseCount, savingsCount] = await Promise.all([
        syncExpenses(),
        syncSavings()
      ]);

      // Actualizar último tiempo de sincronización
      const syncTime = new Date().toISOString();
      setLastSyncTime(syncTime);
      localStorage.setItem('lastSyncTime', syncTime);

      // Limpiar datos sincronizados
      if (expenseCount > 0) localStorage.removeItem('pendingExpenses');
      if (savingsCount > 0) localStorage.removeItem('pendingSavings');

      // Actualizar conteo de cambios pendientes
      countPendingChanges();

      return { expenseCount, savingsCount };
    } catch (error) {
      setSyncError(error.message);
      throw error;
    } finally {
      setIsSyncing(false);
    }
  }, [isSyncing, countPendingChanges]);

  // Sincronizar cuando vuelva la conexión
  useEffect(() => {
    const handleOnline = () => {
      console.log('🌐 Conexión restaurada, iniciando sincronización...');
      syncAll();
    };

    const handleVisibilityChange = () => {
      if (document.visibilityState === 'visible' && navigator.onLine) {
        console.log('📱 App visible, verificando cambios pendientes...');
        countPendingChanges();
      }
    };

    window.addEventListener('online', handleOnline);
    document.addEventListener('visibilitychange', handleVisibilityChange);

    // Cargar último tiempo de sincronización
    const savedSyncTime = localStorage.getItem('lastSyncTime');
    if (savedSyncTime) setLastSyncTime(savedSyncTime);

    // Contar cambios pendientes iniciales
    countPendingChanges();

    // Limpiar event listeners
    return () => {
      window.removeEventListener('online', handleOnline);
      document.removeEventListener('visibilitychange', handleVisibilityChange);
    };
  }, [syncAll, countPendingChanges]);

  // Forzar sincronización manual
  const forceSync = async () => {
    if (!navigator.onLine) {
      throw new Error('No hay conexión a internet');
    }
    return syncAll();
  };

  const contextValue = {
    isSyncing,
    syncError,
    lastSyncTime,
    pendingChanges,
    forceSync,
    isOnline: navigator.onLine
  };

  return (
    <SyncContext.Provider value={contextValue}>
      {children}
    </SyncContext.Provider>
  );
};

// Hook personalizado para usar el contexto
export const useSync = () => {
  const context = useContext(SyncContext);
  if (!context) {
    throw new Error('useSync debe usarse dentro de un SyncProvider');
  }
  return context;
};

// Componente de UI para mostrar estado de sincronización
export const SyncStatus = () => {
  const { isSyncing, syncError, pendingChanges, isOnline, forceSync } = useSync();

  return (
    <div className="fixed bottom-4 right-4">
      {syncError && (
        <div className="mb-2 p-2 bg-red-100 text-red-700 rounded-lg shadow">
          {syncError}
        </div>
      )}

      {(pendingChanges.expenses > 0 || pendingChanges.savings > 0) && (
        <div className="mb-2 p-2 bg-yellow-100 text-yellow-700 rounded-lg shadow">
          Cambios pendientes: {pendingChanges.expenses + pendingChanges.savings}
        </div>
      )}

      <button
        onClick={forceSync}
        disabled={isSyncing || !isOnline}
        className={`p-2 rounded-lg shadow ${
          isSyncing
            ? 'bg-gray-300 cursor-not-allowed'
            : isOnline
            ? 'bg-blue-500 text-white hover:bg-blue-600'
            : 'bg-red-300 cursor-not-allowed'
        }`}
      >
        {isSyncing ? 'Sincronizando...' : isOnline ? 'Sincronizar' : 'Sin conexión'}
      </button>
    </div>
  );
};
