import React, { useState } from 'react';

const AIFinancialAnalysis = () => {
  const [aiInsights, setAiInsights] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  // Sample data - replace with your actual data source
  const expenses = [
    { category: "Alimentación", amount: 300 },
    { category: "Transporte", amount: 150 }
  ];

  const savings = [
    { month: "Enero", amount: 500 },
    { month: "Febrero", amount: 600 }
  ];

  const fetchAIAnalysis = async () => {
    if (!Array.isArray(expenses) || !Array.isArray(savings)) {
      setError('Los datos de gastos o ahorros no son válidos.');
      return;
    }

    setIsLoading(true);
    setError(null);

    try {
      const response = await fetch('http://localhost:11434/api/generate', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          model: 'llama3.2:3b-instruct-q8_0',
          prompt: `Genera un análisis financiero en formato JSON válido. 
          NO INCLUYAS TEXTO ADICIONAL, SOLO EL JSON.

          Datos:
          Gastos: ${JSON.stringify(expenses)}
          Ahorros: ${JSON.stringify(savings)}

          El JSON debe tener exactamente esta estructura:
          {
            "gastosTotales": 450,
            "ahorrosTotales": 1100,
            "analisisGastos": "string con análisis",
            "progresoAhorros": "string con progreso",
            "recomendaciones": [
              "string con recomendación 1",
              "string con recomendación 2",
              "string con recomendación 3"
            ],
            "categoriasMayorGasto": ["categoría 1", "categoría 2"],
            "potencialAhorro": 100
          }`
        })
      });

      const data = await response.json();
      const aiText = data.response.trim();

      console.log('Respuesta del modelo:', aiText);
      try {
        // Intenta parsear directamente
        const parsedData = JSON.parse(aiText);
        setAiInsights(parsedData);
      } catch (e) {
        // Si falla, busca un JSON válido dentro del texto
        try {
          const jsonStartIndex = aiText.indexOf('{');
          const jsonEndIndex = aiText.lastIndexOf('}') + 1;
          if (jsonStartIndex === -1 || jsonEndIndex <= jsonStartIndex) {
            throw new Error('No se encontró un JSON válido en la respuesta');
          }
          const cleanJson = aiText.substring(jsonStartIndex, jsonEndIndex);
          setAiInsights(JSON.parse(cleanJson));
        } catch (innerError) {
          console.error('Error parseando respuesta:', aiText);
          throw new Error('No se pudo procesar la respuesta del modelo');
        }
      }
      setIsLoading(false);

    } catch (error) {
      setError(error.message);
      setIsLoading(false);
    }
  };

  return (
    <div className="space-y-4 p-4">
      <button
        onClick={fetchAIAnalysis}
        disabled={isLoading}
        className={`w-full py-2 px-4 rounded-lg text-white ${
          isLoading ? 'bg-gray-400' : 'bg-blue-600 hover:bg-blue-700'
        }`}
      >
        {isLoading ? 'Analizando...' : 'Obtener Análisis'}
      </button>

      {error && (
        <div className="p-3 bg-red-50 border border-red-200 rounded-lg text-red-700">
          {error}
        </div>
      )}

      {aiInsights && (
        <div className="space-y-4">
          <p className="text-gray-700">
            <span className="font-semibold">Análisis de Gastos:</span> {aiInsights.analisisGastos}
          </p>
          <p className="text-gray-700">
            <span className="font-semibold">Progreso de Ahorros:</span> {aiInsights.progresoAhorros}
          </p>
          <div className="space-y-2">
            <span className="font-semibold">Recomendaciones:</span>
            <ul className="list-disc list-inside">
              {aiInsights.recomendaciones.map((rec, index) => (
                <li key={index} className="text-gray-700">{rec}</li>
              ))}
            </ul>
          </div>
        </div>
      )}
    </div>
  );
};

export default AIFinancialAnalysis;
