import React, { useState } from 'react';
import { savingsService } from '../services/api';

// Utility validation functions
const validateForm = {
  isCurrency: (value) => {
    const currencyRegex = /^\$?(\d{1,3}(,\d{3})*(\.\d{2})?|\d+(\.\d{2})?)$/;
    return currencyRegex.test(value);
  },
  isNumeric: (value, min = null, max = null) => {
    const num = parseFloat(value);
    if (isNaN(num)) return false;
    if (min !== null && num < min) return false;
    if (max !== null && num > max) return false;
    return true;
  },
  isDate: (dateString) => {
    const date = new Date(dateString);
    return date instanceof Date && !isNaN(date) && date > new Date();
  }
};

export const SavingsGoalForm = ({ onAddGoal }) => {
  const [goalData, setGoalData] = useState({
    goalName: '',
    targetAmount: '',
    currentAmount: '',
    targetDate: ''
  });

  const [errors, setErrors] = useState({});
  const [isSubmitting, setIsSubmitting] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setGoalData(prev => ({
      ...prev,
      [name]: value
    }));
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const validateGoalForm = () => {
    const newErrors = {};

    // Validate goal name
    if (!goalData.goalName.trim()) {
      newErrors.goalName = 'El nombre del objetivo es requerido';
    } else if (goalData.goalName.length < 3) {
      newErrors.goalName = 'El nombre debe tener al menos 3 caracteres';
    }

// Validate target amount
if (!goalData.targetAmount) {
  newErrors.targetAmount = 'El monto objetivo es requerido';
} else if (parseFloat(goalData.targetAmount) <= 0) {
  newErrors.targetAmount = 'El monto objetivo debe ser mayor a 0';
}

// Validate current amount
if (!goalData.currentAmount && goalData.currentAmount !== '0') {
  newErrors.currentAmount = 'El monto actual es requerido';
} else if (parseFloat(goalData.currentAmount) < 0) {
  newErrors.currentAmount = 'El monto actual debe ser mayor o igual a 0';
}

    // Validate target date
    if (!goalData.targetDate) {
      newErrors.targetDate = 'La fecha objetivo es requerida';
    } else if (!validateForm.isDate(goalData.targetDate)) {
      newErrors.targetDate = 'La fecha objetivo debe ser posterior a hoy';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);

    try {
      if (validateGoalForm()) {
        const savingData = {
          goalName: goalData.goalName,
          targetAmount: parseFloat(goalData.targetAmount),
          currentAmount: parseFloat(goalData.currentAmount || 0),
          targetDate: new Date(goalData.targetDate).toISOString(),
          status: 'active',
          createdAt: new Date().toISOString(),
          updatedAt: new Date().toISOString()
        };

        try {
          const newSaving = await savingsService.create(savingData);

          // Actualizar localStorage con el nuevo objeto completo
          const savedGoals = JSON.parse(localStorage.getItem('savings') || '[]');
          savedGoals.push({
            ...newSaving,
            synced: true
          });
          localStorage.setItem('savings', JSON.stringify(savedGoals));

          // Llamar al handler del padre con el objeto completo
          onAddGoal(newSaving);

          // Resetear formulario
          setGoalData({
            goalName: '',
            targetAmount: '',
            currentAmount: '',
            targetDate: ''
          });

        } catch (apiError) {
          console.error('Error al guardar en el servidor:', apiError);

          // Guardar localmente si falla el servidor
          const offlineSaving = {
            ...savingData,
            id: `local_${Date.now()}`,
            synced: false,
            status: 'pending'
          };

          const savedGoals = JSON.parse(localStorage.getItem('savings') || '[]');
          savedGoals.push(offlineSaving);
          localStorage.setItem('savings', JSON.stringify(savedGoals));

          onAddGoal(offlineSaving);
          alert('No se pudo conectar con el servidor. Los datos se guardaron localmente y se sincronizarán cuando haya conexión.');
        }
      }
    } catch (error) {
      console.error('Error en el envío del formulario:', error);
      alert('Ocurrió un error al procesar el formulario. Por favor, intente nuevamente.');
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-4">
      <div>
        <label className="block text-sm font-medium text-gray-700 mb-1">
          Nombre del Objetivo
        </label>
        <input
          type="text"
          name="goalName"
          value={goalData.goalName}
          onChange={handleChange}
          className={`w-full p-2 border rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500 ${
            errors.goalName ? 'border-red-500' : 'border-gray-300'
          }`}
          placeholder="Ej: Vacaciones, Casa, Auto"
          disabled={isSubmitting}
        />
        {errors.goalName && (
          <p className="mt-1 text-sm text-red-600">{errors.goalName}</p>
        )}
      </div>

      <div>
        <label className="block text-sm font-medium text-gray-700 mb-1">
          Monto Objetivo
        </label>
        <input
          type="number"
          name="targetAmount"
          value={goalData.targetAmount}
          onChange={handleChange}
          className={`w-full p-2 border rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500 ${
            errors.targetAmount ? 'border-red-500' : 'border-gray-300'
          }`}
          placeholder="Monto total a ahorrar"
          min="0"
          step="1000"
          disabled={isSubmitting}
        />
        {errors.targetAmount && (
          <p className="mt-1 text-sm text-red-600">{errors.targetAmount}</p>
        )}
      </div>

      <div>
        <label className="block text-sm font-medium text-gray-700 mb-1">
          Monto Actual
        </label>
        <input
          type="number"
          name="currentAmount"
          value={goalData.currentAmount}
          onChange={handleChange}
          className={`w-full p-2 border rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500 ${
            errors.currentAmount ? 'border-red-500' : 'border-gray-300'
          }`}
          placeholder="Monto ya ahorrado"
          min="0"
          step="1000"
          disabled={isSubmitting}
        />
        {errors.currentAmount && (
          <p className="mt-1 text-sm text-red-600">{errors.currentAmount}</p>
        )}
      </div>

      <div>
        <label className="block text-sm font-medium text-gray-700 mb-1">
          Fecha Objetivo
        </label>
        <input
          type="date"
          name="targetDate"
          value={goalData.targetDate}
          onChange={handleChange}
          className={`w-full p-2 border rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500 ${
            errors.targetDate ? 'border-red-500' : 'border-gray-300'
          }`}
          disabled={isSubmitting}
        />
        {errors.targetDate && (
          <p className="mt-1 text-sm text-red-600">{errors.targetDate}</p>
        )}
      </div>

      <button
        type="submit"
        disabled={isSubmitting}
        className={`w-full py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white 
          ${isSubmitting 
            ? 'bg-blue-400 cursor-not-allowed' 
            : 'bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500'
          }`}
      >
        {isSubmitting ? 'Guardando...' : 'Crear Meta de Ahorro'}
      </button>
    </form>
  );
};

export default SavingsGoalForm;
