import React, { useState } from 'react';
import { savingsService } from '../services/api';
import { PiggyBank, Calendar, DollarSign, Target } from 'lucide-react';

export const SavingsGoalForm = ({ onAddGoal }) => {
  const [goalData, setGoalData] = useState({
    goalName: '',
    targetAmount: '',
    currentAmount: '',
    targetDate: ''
  });

  const [errors, setErrors] = useState({});
  const [isSubmitting, setIsSubmitting] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setGoalData(prev => ({
      ...prev,
      [name]: value
    }));
    // Limpiar error cuando el usuario modifica el campo
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const validateGoalForm = () => {
    const newErrors = {};

    // Validación del nombre del objetivo
    if (!goalData.goalName.trim()) {
      newErrors.goalName = 'El nombre del objetivo es requerido';
    } else if (goalData.goalName.length < 3) {
      newErrors.goalName = 'El nombre debe tener al menos 3 caracteres';
    }

    // Validación del monto objetivo
    const targetAmount = parseFloat(goalData.targetAmount);
    if (!goalData.targetAmount) {
      newErrors.targetAmount = 'El monto objetivo es requerido';
    } else if (isNaN(targetAmount) || targetAmount <= 0) {
      newErrors.targetAmount = 'El monto objetivo debe ser un número mayor a 0';
    }

    // Validación del monto actual
    const currentAmount = parseFloat(goalData.currentAmount || '0');
    if (isNaN(currentAmount) || currentAmount < 0) {
      newErrors.currentAmount = 'El monto actual debe ser un número mayor o igual a 0';
    }

    // Validación de la fecha objetivo
    if (!goalData.targetDate) {
      newErrors.targetDate = 'La fecha objetivo es requerida';
    } else {
      const selectedDate = new Date(goalData.targetDate);
      const today = new Date();
      today.setHours(0, 0, 0, 0);

      if (selectedDate < today) {
        newErrors.targetDate = 'La fecha objetivo debe ser posterior a hoy';
      }
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);

    try {
      if (validateGoalForm()) {
        const savingData = {
          goalName: goalData.goalName.trim(),
          targetAmount: parseFloat(goalData.targetAmount),
          currentAmount: parseFloat(goalData.currentAmount || '0'),
          targetDate: new Date(goalData.targetDate).toISOString()
        };

        try {
          const newSaving = await savingsService.create(savingData);

          // Actualizar localStorage
          const savedGoals = JSON.parse(localStorage.getItem('savings') || '[]');
          savedGoals.push({
            ...newSaving,
            synced: true
          });
          localStorage.setItem('savings', JSON.stringify(savedGoals));

          onAddGoal(newSaving);

          // Resetear formulario
          setGoalData({
            goalName: '',
            targetAmount: '',
            currentAmount: '',
            targetDate: ''
          });

        } catch (apiError) {
          console.error('Error al guardar en el servidor:', apiError);

          // Guardar localmente si falla el servidor
          const offlineSaving = {
            ...savingData,
            id: `local_${Date.now()}`,
            synced: false,
            status: 'pending'
          };

          const savedGoals = JSON.parse(localStorage.getItem('savings') || '[]');
          savedGoals.push(offlineSaving);
          localStorage.setItem('savings', JSON.stringify(savedGoals));

          onAddGoal(offlineSaving);
          alert('No se pudo conectar con el servidor. Los datos se guardaron localmente y se sincronizarán cuando haya conexión.');
        }
      }
    } catch (error) {
      console.error('Error en el envío del formulario:', error);
      alert('Ocurrió un error al procesar el formulario. Por favor, intente nuevamente.');
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="bg-white p-6 rounded-lg shadow-lg max-w-md mx-auto">
      <h2 className="text-2xl font-bold text-gray-800 mb-6 flex items-center gap-2">
        <PiggyBank className="w-6 h-6 text-blue-600" />
        Nueva Meta de Ahorro
      </h2>

      <form onSubmit={handleSubmit} className="space-y-6">
        <div>
          <label className="flex items-center gap-2 text-sm font-medium text-gray-700 mb-2">
            <Target className="w-4 h-4 text-blue-600" />
            Nombre del Objetivo
          </label>
          <input
            type="text"
            name="goalName"
            value={goalData.goalName}
            onChange={handleChange}
            className={`w-full p-3 border rounded-lg shadow-sm focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-colors ${
              errors.goalName ? 'border-red-500 bg-red-50' : 'border-gray-300'
            }`}
            placeholder="Ej: Vacaciones, Casa, Auto"
            disabled={isSubmitting}
          />
          {errors.goalName && (
            <p className="mt-1 text-sm text-red-600 flex items-center gap-1">
              <span className="inline-block w-1 h-1 bg-red-600 rounded-full"></span>
              {errors.goalName}
            </p>
          )}
        </div>

        <div>
          <label className="flex items-center gap-2 text-sm font-medium text-gray-700 mb-2">
            <DollarSign className="w-4 h-4 text-blue-600" />
            Monto Objetivo
          </label>
          <input
            type="number"
            name="targetAmount"
            value={goalData.targetAmount}
            onChange={handleChange}
            className={`w-full p-3 border rounded-lg shadow-sm focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-colors ${
              errors.targetAmount ? 'border-red-500 bg-red-50' : 'border-gray-300'
            }`}
            placeholder="Monto total a ahorrar"
            min="0"
            step="1000"
            disabled={isSubmitting}
          />
          {errors.targetAmount && (
            <p className="mt-1 text-sm text-red-600 flex items-center gap-1">
              <span className="inline-block w-1 h-1 bg-red-600 rounded-full"></span>
              {errors.targetAmount}
            </p>
          )}
        </div>

        <div>
          <label className="flex items-center gap-2 text-sm font-medium text-gray-700 mb-2">
            <DollarSign className="w-4 h-4 text-blue-600" />
            Monto Actual
          </label>
          <input
            type="number"
            name="currentAmount"
            value={goalData.currentAmount}
            onChange={handleChange}
            className={`w-full p-3 border rounded-lg shadow-sm focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-colors ${
              errors.currentAmount ? 'border-red-500 bg-red-50' : 'border-gray-300'
            }`}
            placeholder="Monto ya ahorrado"
            min="0"
            step="1000"
            disabled={isSubmitting}
          />
          {errors.currentAmount && (
            <p className="mt-1 text-sm text-red-600 flex items-center gap-1">
              <span className="inline-block w-1 h-1 bg-red-600 rounded-full"></span>
              {errors.currentAmount}
            </p>
          )}
        </div>

        <div>
          <label className="flex items-center gap-2 text-sm font-medium text-gray-700 mb-2">
            <Calendar className="w-4 h-4 text-blue-600" />
            Fecha Objetivo
          </label>
          <input
            type="date"
            name="targetDate"
            value={goalData.targetDate}
            onChange={handleChange}
            className={`w-full p-3 border rounded-lg shadow-sm focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-colors ${
              errors.targetDate ? 'border-red-500 bg-red-50' : 'border-gray-300'
            }`}
            disabled={isSubmitting}
          />
          {errors.targetDate && (
            <p className="mt-1 text-sm text-red-600 flex items-center gap-1">
              <span className="inline-block w-1 h-1 bg-red-600 rounded-full"></span>
              {errors.targetDate}
            </p>
          )}
        </div>

        <button
          type="submit"
          disabled={isSubmitting}
          className={`w-full py-3 px-4 border border-transparent rounded-lg shadow-sm text-sm font-medium text-white transition-colors
            ${isSubmitting 
              ? 'bg-blue-400 cursor-not-allowed' 
              : 'bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500'
            }`}
        >
          {isSubmitting ? (
            <span className="flex items-center justify-center gap-2">
              <svg className="animate-spin h-5 w-5 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
              </svg>
              Guardando...
            </span>
          ) : (
            'Crear Meta de Ahorro'
          )}
        </button>
      </form>
    </div>
  );
};

export default SavingsGoalForm;
