// src/components/AIFinancialAnalysis.jsx
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AIFinancialAnalysis = ({ expenses = [], savings = [] }) => {
  const [aiInsights, setAiInsights] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  const fetchAIAnalysis = async () => {
    if (expenses.length === 0 || savings.length === 0) {
      setError('No hay suficientes datos para realizar el análisis');
      return;
    }

    setIsLoading(true);
    setError(null);

    try {
      const response = await axios.post('http://localhost:11434/api/generate', {
        model: "llama3.2:3b-instruct-q8_0",
        prompt: `Actúa como un analista financiero experto. Analiza los siguientes datos financieros y proporciona insights detallados:

        Gastos: ${JSON.stringify(expenses, null, 2)}
        Ahorros: ${JSON.stringify(savings, null, 2)}

        Proporciona un análisis detallado en formato JSON con esta estructura:
        {
          "gastosTotales": número,
          "ahorrosTotales": número,
          "analisisGastos": string,
          "progresoAhorros": string,
          "recomendaciones": [string, string, string],
          "categoriasMayorGasto": [string, string, string],
          "potencialAhorro": número
        }`,
        stream: false,
      });

      if (response.status !== 200) {
        throw new Error('Error en la respuesta de la API');
      }

      // Verificar si response.data tiene el formato esperado
      if (!response.data || !response.data.response) {
        throw new Error('La respuesta de la IA está vacía o no tiene el formato esperado.');
      }

      try {
        const aiResponse = JSON.parse(response.data.response);

        // Validar que la respuesta tenga la estructura correcta
        if (
          typeof aiResponse.gastosTotales !== 'number' ||
          typeof aiResponse.ahorrosTotales !== 'number' ||
          typeof aiResponse.analisisGastos !== 'string' ||
          typeof aiResponse.progresoAhorros !== 'string' ||
          !Array.isArray(aiResponse.recomendaciones) ||
          !Array.isArray(aiResponse.categoriasMayorGasto) ||
          typeof aiResponse.potencialAhorro !== 'number'
        ) {
          throw new Error('La respuesta de la IA no cumple con la estructura esperada.');
        }

        setAiInsights(aiResponse);
      } catch (parseError) {
        console.error('Error al parsear la respuesta de la IA:', parseError);
        setError('La respuesta de la IA no es válida.');
      }
    } catch (err) {
      console.error('Error en el análisis financiero:', err);
      setError('No se pudo conectar con el servicio de IA. Verifica que Ollama esté corriendo.');
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    if (expenses.length > 0 && savings.length > 0) {
      fetchAIAnalysis();
    }
  }, [expenses, savings]);

  return (
    <div className="bg-white p-6 rounded-lg shadow-md">
      <h2 className="text-2xl font-bold mb-6">Análisis Financiero IA</h2>

      {isLoading && (
        <div className="animate-pulse space-y-4">
          <div className="h-4 bg-gray-200 rounded w-3/4"></div>
          <div className="h-4 bg-gray-200 rounded w-1/2"></div>
          <div className="h-4 bg-gray-200 rounded w-5/6"></div>
        </div>
      )}

      {error && (
        <div className="bg-red-50 p-4 rounded-lg">
          <p className="text-red-600">{error}</p>
          <button
            onClick={fetchAIAnalysis}
            className="mt-2 px-4 py-2 bg-red-600 text-white rounded hover:bg-red-700"
          >
            Reintentar
          </button>
        </div>
      )}

      {aiInsights && (
        <div className="space-y-6">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div className="p-4 bg-blue-50 rounded-lg">
              <h3 className="font-semibold text-lg mb-2">Gastos Totales</h3>
              <p className="text-2xl font-bold text-blue-600">
                ${aiInsights.gastosTotales.toLocaleString()}
              </p>
            </div>
            <div className="p-4 bg-green-50 rounded-lg">
              <h3 className="font-semibold text-lg mb-2">Ahorros Totales</h3>
              <p className="text-2xl font-bold text-green-600">
                ${aiInsights.ahorrosTotales.toLocaleString()}
              </p>
            </div>
          </div>

          <div>
            <h3 className="font-semibold text-lg mb-2">Análisis de Gastos</h3>
            <p className="text-gray-700">{aiInsights.analisisGastos}</p>
          </div>

          <div>
            <h3 className="font-semibold text-lg mb-2">Progreso de Ahorros</h3>
            <p className="text-gray-700">{aiInsights.progresoAhorros}</p>
          </div>

          <div>
            <h3 className="font-semibold text-lg mb-2">Categorías con Mayor Gasto</h3>
            <ul className="list-disc list-inside space-y-2">
              {aiInsights.categoriasMayorGasto.map((categoria, index) => (
                <li key={index} className="text-gray-700">{categoria}</li>
              ))}
            </ul>
          </div>

          <div>
            <h3 className="font-semibold text-lg mb-2">Recomendaciones</h3>
            <ul className="list-disc list-inside space-y-2">
              {aiInsights.recomendaciones.map((rec, index) => (
                <li key={index} className="text-gray-700">{rec}</li>
              ))}
            </ul>
          </div>

          <div>
            <h3 className="font-semibold text-lg mb-2">Potencial de Ahorro Mensual</h3>
            <p className="text-2xl font-bold text-green-600">
              ${aiInsights.potencialAhorro.toLocaleString()}
            </p>
          </div>
        </div>
      )}

      {!aiInsights && !error && !isLoading && (
        <p className="text-gray-500">No hay análisis disponible.</p>
      )}
    </div>
  );
};

export default AIFinancialAnalysis;
