import React, { useState, useEffect, useMemo, useCallback } from 'react';
import { PieChart, Pie, Cell, ResponsiveContainer, Tooltip, Legend } from 'recharts';
import { expensesService, savingsService } from './services/api';
import { SavingsGoalForm } from './components/SavingsGoalForm';
import { ExpenseForm } from './components/ExpenseForm';
import AIFinancialAnalysis from './components/AIFinancialAnalysis';
import _ from 'lodash';

// Constantes para colores del gr谩fico
const COLORS = ['#0088FE', '#00C49F', '#FFBB28', '#FF8042', '#8884D8'];

// Componentes de los iconos
const Icons = {
  Home: () => <div></div>,
  Savings: () => <div></div>,
  Expenses: () => <div></div>,
  Analysis: () => <div></div>,
  Settings: () => <div>锔</div>,
};

// Componente de Distribuci贸n de Gastos
const ExpenseDistribution = ({ expenses = [] }) => {
  const chartData = useMemo(() => {
    if (!expenses?.length) return [];
    const groupedExpenses = _.groupBy(expenses, 'category');
    return Object.entries(groupedExpenses).map(([category, items]) => ({
      name: category ? category.charAt(0).toUpperCase() + category.slice(1) : 'Otros',
      value: _.sumBy(items, 'amount') || 0,
    }));
  }, [expenses]);

  const totalExpenses = useMemo(() => _.sumBy(chartData, 'value') || 0, [chartData]);

  if (!expenses?.length) {
    return (
      <div>
        <h2>Distribuci贸n de Gastos</h2>
        <p>No hay gastos registrados.</p>
      </div>
    );
  }

  return (
    <div>
      <h2>Distribuci贸n de Gastos</h2>
      <ResponsiveContainer width="100%" height={300}>
        <PieChart>
          <Pie
            data={chartData}
            cx="50%"
            cy="50%"
            innerRadius={60}
            outerRadius={80}
            fill="#8884d8"
            dataKey="value"
            label={({ name, value }) => `${name} (${((value / totalExpenses) * 100).toFixed(1)}%)`}
          >
            {chartData.map((entry, index) => (
              <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
            ))}
          </Pie>
          <Tooltip />
          <Legend />
        </PieChart>
      </ResponsiveContainer>
      <div>
        <h3>Resumen por Categor铆a</h3>
        <ul>
          {chartData.map((item, index) => (
            <li key={`item-${index}`}>
              {item.name}: ${item.value.toLocaleString()}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

// Componente de Overview
const OverviewPage = ({ expenses = [], savings = [] }) => {
  const totalSavings = useMemo(() =>
    savings.reduce(
      (acc, goal) =>
        acc + parseFloat(goal.currentAmount || goal.current_amount || 0),
      0
    ),
    [savings]
  );

  const monthlyExpenses = useMemo(() => {
    if (!Array.isArray(expenses)) return 0;
    const currentDate = new Date();
    return expenses
      .filter((expense) => {
        try {
          const expenseDate = new Date(expense.date);
          return (
            expenseDate.getMonth() === currentDate.getMonth() &&
            expenseDate.getFullYear() === currentDate.getFullYear()
          );
        } catch (error) {
          return false;
        }
      })
      .reduce((acc, expense) => acc + parseFloat(expense.amount || 0), 0);
  }, [expenses]);

  return (
    <div className="p-4">
      {/* Gastos Mensuales */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">
        <div className="bg-gradient-to-r from-red-500 to-orange-500 text-white p-4 rounded-lg shadow-md flex items-center justify-between">
          <div>
            <h3 className="text-lg font-semibold">Gastos Mensuales</h3>
            <p className="text-2xl">${monthlyExpenses.toLocaleString()}</p>
          </div>
          <div className="w-12 h-12 bg-white rounded-full flex items-center justify-center">
            <svg
              xmlns="http://www.w3.org/2000/svg"
              className="h-6 w-6 text-red-500"
              fill="none"
              viewBox="0 0 24 24"
              stroke="currentColor"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth={2}
                d="M12 6v6m0 0v6m0-6h6m-6 0H6"
              />
            </svg>
          </div>
        </div>

        {/* Metas de Ahorro */}
        <div className="col-span-2">
          <div className="bg-gradient-to-r from-green-500 to-emerald-500 text-white p-4 rounded-lg shadow-md mb-4">
            <div className="flex justify-between items-center">
              <div>
                <h3 className="text-lg font-semibold">Metas de Ahorro</h3>
                <p className="text-2xl">{savings.length} metas activas</p>
              </div>
              <div className="w-12 h-12 bg-white rounded-full flex items-center justify-center">
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  className="h-6 w-6 text-green-500"
                  fill="none"
                  viewBox="0 0 24 24"
                  stroke="currentColor"
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth={2}
                    d="M12 6v6m0 0v6m0-6h6m-6 0H6"
                  />
                </svg>
              </div>
            </div>
          </div>

          <div className="space-y-4">
            {savings.length > 0 ? (
              savings.map((goal) => (
                <div
                  key={goal.id}
                  className="bg-white p-4 rounded-lg shadow-md"
                >
                  <div className="flex justify-between items-center mb-2">
                    <p className="font-semibold text-lg">{goal.goalName || goal.goal_name}</p>
                    <div className="text-sm text-gray-500">
                      Meta: ${(parseFloat(goal.targetAmount || goal.target_amount) || 0).toLocaleString()}
                    </div>
                  </div>

                  <div className="mb-2">
                    <div className="flex justify-between text-sm text-gray-600 mb-1">
                      <span>Progreso</span>
                      <span>
                        {Math.round(
                          ((parseFloat(goal.currentAmount || goal.current_amount) || 0) /
                            (parseFloat(goal.targetAmount || goal.target_amount) || 1)) *
                            100
                        )}%
                      </span>
                    </div>
                    <div className="w-full bg-gray-200 rounded-full h-2">
                      <div
                        className="bg-green-500 h-2 rounded-full"
                        style={{
                          width: `${Math.min(
                            Math.round(
                              ((parseFloat(goal.currentAmount || goal.current_amount) || 0) /
                                (parseFloat(goal.targetAmount || goal.target_amount) || 1)) *
                                100
                            ),
                            100
                          )}%`,
                        }}
                      ></div>
                    </div>
                  </div>

                  <div className="text-sm text-gray-600">
                    Ahorrado: ${(parseFloat(goal.currentAmount || goal.current_amount) || 0).toLocaleString()}
                  </div>
                </div>
              ))
            ) : (
              <div className="bg-white p-4 rounded-lg shadow-md text-center text-gray-500">
                No hay metas de ahorro activas.
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Gr谩ficos y An谩lisis */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        {/* Distribuci贸n de Gastos */}
        <div className="bg-white p-4 rounded-lg shadow-md">
          <ExpenseDistribution expenses={expenses} />
        </div>

        {/* An谩lisis Financiero con IA */}
        <div className="bg-white p-4 rounded-lg shadow-md">
          <h3 className="text-lg font-semibold mb-4">An谩lisis Financiero con IA</h3>
          <AIFinancialAnalysis expenses={expenses} savings={savings} />
        </div>
      </div>
    </div>
  );
};

// Componente de Configuraci贸n
const SettingsPage = () => (
  <div>
    <h2>Configuraciones</h2>
    <p>Preferencias</p>
    <p>Pr贸ximamente m谩s configuraciones</p>
  </div>
);

// Componente Principal
const App = () => {
  const [activeSection, setActiveSection] = useState('overview');
  const [expenses, setExpenses] = useState([]);
  const [savings, setSavings] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  // Sincronizaci贸n de datos pendientes
  const syncPendingData = useCallback(async () => {
    try {
      const pendingExpenses = expenses.filter((exp) => !exp.synced);
      const pendingSavings = savings.filter((sav) => !sav.synced);

      for (const expense of pendingExpenses) {
        try {
          const syncedExpense = await expensesService.create({
            ...expense,
            date: new Date(expense.date).toISOString().split('T')[0],
          });
          setExpenses((prev) =>
            prev.map((exp) =>
              exp.id === expense.id ? { ...syncedExpense, synced: true } : exp
            )
          );
        } catch (error) {
          console.error('Error sincronizando gasto:', error);
        }
      }

      for (const saving of pendingSavings) {
        try {
          const targetDate = new Date(saving.targetDate || saving.target_date);
          if (isNaN(targetDate.getTime())) {
            console.error('Fecha inv谩lida para ahorro:', saving);
            continue;
          }
          const savingData = {
            goal_name: saving.goalName || saving.goal_name,
            target_amount: parseFloat(saving.targetAmount || saving.target_amount),
            current_amount: parseFloat(saving.currentAmount || saving.current_amount || 0),
            target_date: targetDate.toISOString().split('T')[0],
          };
          const syncedSaving = await savingsService.create(savingData);
          setSavings((prev) =>
            prev.map((sav) =>
              sav.id === saving.id ? { ...syncedSaving, synced: true } : sav
            )
          );
        } catch (error) {
          console.error('Error sincronizando ahorro:', error);
        }
      }

      localStorage.setItem('expenses', JSON.stringify(expenses));
      localStorage.setItem('savings', JSON.stringify(savings));
    } catch (error) {
      console.error('Error en sincronizaci贸n:', error);
    }
  }, [expenses, savings]);

  // Agregar nuevo gasto
  const handleAddExpense = useCallback((expenseData) => {
    const newExpense = {
      ...expenseData,
      id: Date.now().toString(),
      synced: false,
    };
    setExpenses((prev) => {
      const updatedExpenses = [...prev, newExpense];
      localStorage.setItem('expenses', JSON.stringify(updatedExpenses));
      return updatedExpenses;
    });
    try {
      expensesService.create(newExpense);
    } catch (error) {
      console.error('Error guardando gasto en servidor:', error);
    }
  }, []);

  // Agregar nueva meta de ahorro
  const handleAddSavingsGoal = useCallback(async (savingsData) => {
    try {
      const targetDate = new Date(savingsData.targetDate || savingsData.target_date);
      if (isNaN(targetDate.getTime())) {
        throw new Error('Fecha inv谩lida');
      }
      const newSavingsGoal = {
        ...savingsData,
        id: Date.now().toString(),
        current_amount: 0,
        target_date: targetDate.toISOString().split('T')[0],
        synced: false,
      };
      setSavings((prev) => {
        const updatedSavings = [...prev, newSavingsGoal];
        localStorage.setItem('savings', JSON.stringify(updatedSavings));
        return updatedSavings;
      });

      try {
        const syncedSaving = await savingsService.create({
          goal_name: newSavingsGoal.goalName || newSavingsGoal.goal_name,
          target_amount: parseFloat(newSavingsGoal.targetAmount || newSavingsGoal.target_amount),
          current_amount: 0,
          target_date: newSavingsGoal.target_date,
        });
        setSavings((prev) =>
          prev.map((sav) =>
            sav.id === newSavingsGoal.id ? { ...syncedSaving, synced: true } : sav
          )
        );
      } catch (error) {
        console.error('Error guardando ahorro en servidor:', error);
      }
    } catch (error) {
      console.error('Error agregando meta de ahorro:', error);
    }
  }, []);

  // Cargar datos iniciales
  useEffect(() => {
    const fetchData = async () => {
      setIsLoading(true);
      setError(null);
      try {
        const localExpenses = JSON.parse(localStorage.getItem('expenses') || '[]');
        const localSavings = JSON.parse(localStorage.getItem('savings') || '[]');

        setExpenses(localExpenses);
        setSavings(localSavings);

        const [expensesData, savingsData] = await Promise.all([
          expensesService.getAll(),
          savingsService.getAll(),
        ]);

        if (!_.isEqual(expensesData, localExpenses)) {
          setExpenses(Array.isArray(expensesData) ? expensesData : []);
          localStorage.setItem('expenses', JSON.stringify(expensesData));
        }

        if (!_.isEqual(savingsData, localSavings)) {
          setSavings(Array.isArray(savingsData) ? savingsData : []);
          localStorage.setItem('savings', JSON.stringify(savingsData));
        }
      } catch (error) {
        console.error('Error cargando datos:', error);
        setError('Error al cargar datos del servidor. Mostrando datos locales.');
      } finally {
        setIsLoading(false);
      }
    };
    fetchData();
  }, []);

  // Sincronizaci贸n cuando hay conexi贸n
  useEffect(() => {
    const handleOnline = () => {
      const hasPendingData = [...expenses, ...savings].some((item) => !item.synced);
      if (hasPendingData) {
        syncPendingData();
      }
    };
    window.addEventListener('online', handleOnline);
    return () => window.removeEventListener('online', handleOnline);
  }, [expenses, savings, syncPendingData]);

  // Secciones
  const sections = useMemo(
    () => [
      {
        id: 'overview',
        icon: Icons.Home,
        title: 'Resumen',
        color: 'bg-gradient-to-r from-blue-500 to-teal-400',
        component: () => <OverviewPage expenses={expenses} savings={savings} />,
      },
      {
        id: 'savings',
        icon: Icons.Savings,
        title: 'Ahorros',
        color: 'bg-gradient-to-r from-green-400 to-emerald-500',
        component: () => <SavingsGoalForm onAddSavings={handleAddSavingsGoal} />,
      },
      {
        id: 'expenses',
        icon: Icons.Expenses,
        title: 'Gastos',
        color: 'bg-gradient-to-r from-yellow-400 to-orange-500',
        component: () => <ExpenseForm onAddExpense={handleAddExpense} />,
      },
      {
        id: 'analysis',
        icon: Icons.Analysis,
        title: 'An谩lisis',
        color: 'bg-gradient-to-r from-purple-500 to-indigo-600',
        component: () => <AIFinancialAnalysis expenses={expenses} savings={savings} />,
      },
      {
        id: 'settings',
        icon: Icons.Settings,
        title: 'Config',
        color: 'bg-gradient-to-r from-gray-400 to-gray-600',
        component: SettingsPage,
      },
    ],
    [expenses, savings, handleAddExpense, handleAddSavingsGoal]
  );

  return (
    <div className="flex h-screen">
      {/* Barra lateral */}
      <div className="w-64 bg-gray-800 text-white flex flex-col space-y-4 p-4">
        {sections.map((section) => (
          <button
            key={section.id}
            onClick={() => setActiveSection(section.id)}
            className={`p-3 rounded-xl transition-all duration-300 ease-in-out transform hover:scale-110 hover:shadow-xl focus:outline-none group ${
              activeSection === section.id
                ? section.color + ' text-white'
                : 'bg-gray-700 text-gray-300 hover:bg-gray-600'
            }`}
          >
            {section.icon()}
            <span className="ml-2">{section.title}</span>
          </button>
        ))}
      </div>

      {/* rea principal de contenido */}
      <div className="flex-1 p-4">
        {error && <div className="text-red-500">{error}</div>}
        {isLoading ? (
          <div>Cargando...</div>
        ) : (
          sections.find((section) => section.id === activeSection)?.component()
        )}
      </div>
    </div>
  );
};

export default App;
