import React, { useState } from 'react';

const AIFinancialAnalysis = () => {
  const [aiInsights, setAiInsights] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  // Sample data - replace with your actual data source
  const expenses = [
    { category: "Alimentación", amount: 300 },
    { category: "Transporte", amount: 150 }
  ];

  const savings = [
    { month: "Enero", amount: 500 },
    { month: "Febrero", amount: 600 }
  ];

  const fetchAIAnalysis = async () => {
    if (!Array.isArray(expenses) || !Array.isArray(savings)) {
      setError('Los datos de gastos o ahorros no son válidos.');
      return;
    }

    setIsLoading(true);
    setError(null);

    try {
      // Simulate API call
      const mockResponse = {
        gastosTotales: 450,
        ahorrosTotales: 1100,
        analisisGastos: "Gastos controlados",
        progresoAhorros: "Buen progreso",
        recomendaciones: ["Reducir gastos", "Aumentar ahorros"],
        categoriasMayorGasto: ["Alimentación", "Transporte"],
        potencialAhorro: 100
      };

      setTimeout(() => {
        setAiInsights(mockResponse);
        setIsLoading(false);
      }, 1000);

    } catch (error) {
      setError(error.message);
      setIsLoading(false);
    }
  };

  return (
    <div className="space-y-4 p-4">
      <button
        onClick={fetchAIAnalysis}
        disabled={isLoading}
        className={`w-full py-2 px-4 rounded-lg text-white ${
          isLoading ? 'bg-gray-400' : 'bg-blue-600 hover:bg-blue-700'
        }`}
      >
        {isLoading ? 'Analizando...' : 'Obtener Análisis'}
      </button>

      {error && (
        <div className="p-3 bg-red-50 border border-red-200 rounded-lg text-red-700">
          {error}
        </div>
      )}

      {aiInsights && (
        <div className="space-y-4">
          <p className="text-gray-700">
            <span className="font-semibold">Análisis de Gastos:</span> {aiInsights.analisisGastos}
          </p>
          <p className="text-gray-700">
            <span className="font-semibold">Progreso de Ahorros:</span> {aiInsights.progresoAhorros}
          </p>
          <div className="space-y-2">
            <span className="font-semibold">Recomendaciones:</span>
            <ul className="list-disc list-inside">
              {aiInsights.recomendaciones.map((rec, index) => (
                <li key={index} className="text-gray-700">{rec}</li>
              ))}
            </ul>
          </div>
        </div>
      )}
    </div>
  );
};

export default AIFinancialAnalysis;
