import React, { useState, useEffect } from 'react';
import { expensesService, savingsService } from './services/api';
import ExpenseForm from './components/ExpenseForm';
import { SavingsGoalForm } from './components/SavingsGoalForm';
import AIFinancialAnalysis from './components/AIFinancialAnalysis';

const SettingsPage = () => (
  <div className="space-y-8">
    <h1 className="text-4xl font-bold text-gray-800 mb-6">Configuraciones</h1>
    <div className="bg-white rounded-2xl shadow-lg p-6">
      <h2 className="text-2xl font-semibold mb-4">Preferencias</h2>
      <div className="space-y-4">
        <p>Próximamente más configuraciones</p>
      </div>
    </div>
  </div>
);


// Páginas con lógica implementada (mantengo las páginas existentes)
const OverviewPage = ({ expenses, savings }) => {
  const totalSavings = savings.reduce((acc, goal) => acc + goal.currentAmount, 0);
  const monthlyExpenses = expenses
    .filter(expense => {
      const expenseDate = new Date(expense.date);
      const currentDate = new Date();
      return expenseDate.getMonth() === currentDate.getMonth() &&
             expenseDate.getFullYear() === currentDate.getFullYear();
    })
    .reduce((acc, expense) => acc + expense.amount, 0);

  return (
    <div className="space-y-8">
      <h1 className="text-4xl font-bold text-gray-800 mb-6">Resumen Económico</h1>
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        <div className="bg-gradient-to-r from-blue-500 to-teal-400 text-white p-6 rounded-2xl shadow-lg">
          <h3 className="text-sm opacity-75 mb-2">Ahorros Totales</h3>
          <p className="text-2xl font-bold">${totalSavings.toLocaleString()}</p>
        </div>
        <div className="bg-gradient-to-r from-yellow-400 to-orange-500 text-white p-6 rounded-2xl shadow-lg">
          <h3 className="text-sm opacity-75 mb-2">Gastos Mensuales</h3>
          <p className="text-2xl font-bold">${monthlyExpenses.toLocaleString()}</p>
        </div>
        <div className="bg-gradient-to-r from-green-400 to-emerald-500 text-white p-6 rounded-2xl shadow-lg">
          <h3 className="text-sm opacity-75 mb-2">Metas de Ahorro</h3>
          <p className="text-2xl font-bold">{savings.length} Metas</p>
        </div>
      </div>
    </div>
  );
};

// Resto de las páginas (SavingsPage, ExpensesPage, AnalysisPage, SettingsPage) 
// se mantienen igual que en el código original

// Icons (se mantienen igual)
const Icons = {
  // ... (código de iconos original)
};

const App = () => {
  const [activeSection, setActiveSection] = useState('overview');
  const [expenses, setExpenses] = useState([]);
  const [savings, setSavings] = useState([]);

  // Cargar datos al montar el componente
  useEffect(() => {
    const fetchData = async () => {
      try {
        const expensesData = await expensesService.getAll();
        const savingsData = await savingsService.getAll();

        setExpenses(expensesData);
        setSavings(savingsData);
      } catch (error) {
        console.error('Error cargando datos:', error);
      }
    };

    fetchData();
  }, []);

  // Sincronización al iniciar la aplicación
  useEffect(() => {
    // Cargar datos desde localStorage
    const savedExpenses = JSON.parse(localStorage.getItem('expenses') || '[]');
    const savedSavings = JSON.parse(localStorage.getItem('savings') || '[]');

    setExpenses(savedExpenses);
    setSavings(savedSavings);

    // Sincronizar con backend
    const syncData = async () => {
      try {
        // Sincronizar gastos pendientes
        const pendingExpenses = savedExpenses.filter(exp => !exp.synced);
        for (let expense of pendingExpenses) {
          try {
            const syncedExpense = await expensesService.create(expense);
            const updatedExpenses = expenses.map(exp => 
              exp.id === expense.id 
                ? { ...syncedExpense, synced: true } 
                : exp
            );
            setExpenses(updatedExpenses);
            localStorage.setItem('expenses', JSON.stringify(updatedExpenses));
          } catch (error) {
            console.error('Error sincronizando gasto:', error);
          }
        }

        // Sincronizar ahorros pendientes
        const pendingSavings = savedSavings.filter(sav => !sav.synced);
        for (let saving of pendingSavings) {
          try {
            const syncedSaving = await savingsService.create(saving);
            const updatedSavings = savings.map(sav => 
              sav.id === saving.id 
                ? { ...syncedSaving, synced: true } 
                : sav
            );
            setSavings(updatedSavings);
            localStorage.setItem('savings', JSON.stringify(updatedSavings));
          } catch (error) {
            console.error('Error sincronizando ahorro:', error);
          }
        }
      } catch (error) {
        console.error('Error en sincronización:', error);
      }
    };

    syncData();
  }, []);

  // Manejadores de eventos
  const handleAddExpense = async (expenseData) => {
    const newExpense = {
      ...expenseData,
      id: Date.now().toString(),
      date: new Date(expenseData.date).toISOString(),
      synced: false // Marcar como no sincronizado
    };

    try {
      // Intentar guardar en backend
      const syncedExpense = await expensesService.create(newExpense);

      // Actualizar estado y localStorage
      const updatedExpenses = [...expenses, { ...syncedExpense, synced: true }];
      setExpenses(updatedExpenses);
      localStorage.setItem('expenses', JSON.stringify(updatedExpenses));
    } catch (error) {
      // Si falla, guardar localmente
      const updatedExpenses = [...expenses, newExpense];
      setExpenses(updatedExpenses);
      localStorage.setItem('expenses', JSON.stringify(updatedExpenses));
      console.error('Error guardando gasto:', error);
    }
  };

  const handleAddSavingsGoal = async (savingsData) => {
    const newSavingsGoal = {
      ...savingsData,
      id: Date.now().toString(),
      currentAmount: 0,
      synced: false // Marcar como no sincronizado
    };

    try {
      // Intentar guardar en backend
      const syncedSaving = await savingsService.create(newSavingsGoal);

      // Actualizar estado y localStorage
      const updatedSavings = [...savings, { ...syncedSaving, synced: true }];
      setSavings(updatedSavings);
      localStorage.setItem('savings', JSON.stringify(updatedSavings));
    } catch (error) {
      // Si falla, guardar localmente
      const updatedSavings = [...savings, newSavingsGoal];
      setSavings(updatedSavings);
      localStorage.setItem('savings', JSON.stringify(updatedSavings));
      console.error('Error guardando meta de ahorro:', error);
    }
  };

  const sections = [
    { 
      id: 'overview', 
      icon: Icons.Home, 
      title: 'Resumen', 
      color: 'bg-gradient-to-r from-blue-500 to-teal-400',
      component: () => <OverviewPage expenses={expenses} savings={savings} />
    },
    { 
      id: 'savings', 
      icon: Icons.Savings, 
      title: 'Ahorros', 
      color: 'bg-gradient-to-r from-green-400 to-emerald-500',
      component: () => <SavingsGoalForm savings={savings} onAddGoal={handleAddSavingsGoal} />
    },
    { 
      id: 'expenses', 
      icon: Icons.Expenses, 
      title: 'Gastos', 
      color: 'bg-gradient-to-r from-yellow-400 to-orange-500',
      component: () => <ExpenseForm expenses={expenses} onAddExpense={handleAddExpense} />
    },
    { 
      id: 'analysis', 
      icon: Icons.Analysis, 
      title: 'Análisis', 
      color: 'bg-gradient-to-r from-purple-500 to-indigo-600',
      component: () => <AIFinancialAnalysis expenses={expenses} savings={savings} />
    },
    { 
      id: 'settings', 
      icon: Icons.Settings, 
      title: 'Config', 
      color: 'bg-gradient-to-r from-gray-400 to-gray-600',
      component: SettingsPage
    }
  ];



  const ActiveComponent = sections.find(section => section.id === activeSection)?.component || sections[0].component;

  return (
    <div className="flex h-screen bg-gray-50">
      {/* Sidebar */}
      <div className="w-24 bg-white shadow-lg p-4 flex flex-col space-y-4">
        {sections.map((section) => (
          <button
            key={section.id}
            onClick={() => setActiveSection(section.id)}
            className={`
              p-3 rounded-xl transition-all duration-300 ease-in-out transform
              hover:scale-110 hover:shadow-xl focus:outline-none group
              ${activeSection === section.id 
                ? section.color + ' text-white' 
                : 'bg-gray-100 text-gray-600 hover:bg-gray-200'}
            `}
          >
            <section.icon className="w-6 h-6 mx-auto" />
            <span className="text-xs block text-center mt-1 opacity-75">
              {section.title}
            </span>
          </button>
        ))}
      </div>

      {/* Main Content Area */}
      <div className="flex-1 p-8 overflow-y-auto bg-gray-50">
        <ActiveComponent />
      </div>
    </div>
  );
};

export default App;
