import React, { useState, useEffect, useMemo } from 'react';
import { PieChart, Pie, Cell, ResponsiveContainer, Tooltip, Legend } from 'recharts';
import { expensesService, savingsService } from './services/api';
import { SavingsGoalForm } from './components/SavingsGoalForm';
import { ExpenseForm } from './components/ExpenseForm';
import AIFinancialAnalysis from './components/AIFinancialAnalysis';
import _ from 'lodash';

// Constantes para colores del gráfico
const COLORS = [
  '#0088FE', '#00C49F', '#FFBB28', '#FF8042', '#8884D8', 
  '#82CA9D', '#F06292', '#BA68C8', '#4DB6AC', '#FFB74D'
];

// Componentes de los iconos - No cambian
const Icons = {
  Home: () => (
    <svg className="w-6 h-6" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor">
      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 12l2-2m0 0l7-7 7 7M5 10v10a1 1 0 001 1h3m10-11l2 2m-2-2v10a1 1 0 01-1 1h-3m-6 0a1 1 0 001-1v-4a1 1 0 011-1h2a1 1 0 011 1v4a1 1 0 001 1m-6 0h6" />
    </svg>
  ),
  Savings: () => (
    <svg className="w-6 h-6" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor">
      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 10h18M7 15h1m4 0h1m-7-5v8a2 2 0 002 2h10a2 2 0 002-2v-8a2 2 0 00-2-2H5a2 2 0 00-2 2zm8-2v2h2V8h-2z" />
    </svg>
  ),
  Expenses: () => (
    <svg className="w-6 h-6" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor">
      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 14l6-6m-5.5.5h.01m4.99 5h.01M19 21V5a2 2 0 00-2-2H7a2 2 0 00-2 2v16l3.5-2 3.5 2 3.5-2 3.5 2zM10 8.5a.5.5 0 11-1 0 .5.5 0 011 0zm5 5a.5.5 0 11-1 0 .5.5 0 011 0z" />
    </svg>
  ),
  Analysis: () => (
    <svg className="w-6 h-6" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor">
      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 19v-6a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2a2 2 0 002-2zm0 0V9a2 2 0 012-2h2a2 2 0 012 2v10m-6 0a2 2 0 002 2h2a2 2 0 002-2m0 0V5a2 2 0 012-2h2a2 2 0 012 2v14a2 2 0 01-2 2h-2a2 2 0 01-2-2z" />
    </svg>
  ),
  Settings: () => (
    <svg className="w-6 h-6" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor">
      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M10.325 4.317c.426-1.756 2.924-1.756 3.35 0a1.724 1.724 0 002.573 1.066c1.543-.94 3.31.826 2.37 2.37a1.724 1.724 0 001.065 2.572c1.756.426 1.756 2.924 0 3.35a1.724 1.724 0 00-1.066 2.573c.94 1.543-.826 3.31-2.37 2.37a1.724 1.724 0 00-2.572 1.065c-.426 1.756-2.924 1.756-3.35 0a1.724 1.724 0 00-2.573-1.066c-1.543.94-3.31-.826-2.37-2.37a1.724 1.724 0 00-1.065-2.572c-1.756-.426-1.756-2.924 0-3.35a1.724 1.724 0 001.066-2.573c-.94-1.543.826-3.31 2.37-2.37.947.522 2.11-.274 2.572-1.065z" />
      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 12a3 3 0 11-6 0 3 3 0 016 0z" />
    </svg>
  )
};

// Componente Principal
const App = () => {
  const [activeSection, setActiveSection] = useState('overview');
  const [expenses, setExpenses] = useState([]);
  const [savings, setSavings] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  // Cargar datos iniciales
  useEffect(() => {
    const fetchData = async () => {
      setIsLoading(true);
      setError(null);

      try {
        // Primero cargar datos del localStorage
        const localExpenses = JSON.parse(localStorage.getItem('expenses') || '[]');
        const localSavings = JSON.parse(localStorage.getItem('savings') || '[]');

        setExpenses(localExpenses);
        setSavings(localSavings);

        // Luego intentar obtener datos del servidor
        const [expensesData, savingsData] = await Promise.all([
          expensesService.getAll(),
          savingsService.getAll()
        ]);

        // Actualizar con datos del servidor si son diferentes
        if (!_.isEqual(expensesData, localExpenses)) {
          setExpenses(expensesData);
          localStorage.setItem('expenses', JSON.stringify(expensesData));
        }

        if (!_.isEqual(savingsData, localSavings)) {
          setSavings(savingsData);
          localStorage.setItem('savings', JSON.stringify(savingsData));
        }
      } catch (error) {
        console.error('Error cargando datos:', error);
        setError('Error al cargar datos del servidor. Mostrando datos locales.');
      } finally {
        setIsLoading(false);
      }
    };

    fetchData();
  }, []);

  // Función para sincronizar datos pendientes
  const syncPendingData = async () => {
    try {
      const pendingExpenses = expenses.filter(exp => !exp.synced);
      const pendingSavings = savings.filter(sav => !sav.synced);

      // Sincronizar gastos pendientes
      for (const expense of pendingExpenses) {
        try {
          const syncedExpense = await expensesService.create({
            ...expense,
            date: new Date(expense.date).toISOString().split('T')[0]
          });

          setExpenses(prev => 
            prev.map(exp => 
              exp.id === expense.id ? { ...syncedExpense, synced: true } : exp
            )
          );
        } catch (error) {
          console.error('Error sincronizando gasto:', error);
        }
      }

      // Sincronizar ahorros pendientes
      for (const saving of pendingSavings) {
        try {
          const targetDate = new Date(saving.targetDate || saving.target_date);
          if (isNaN(targetDate.getTime())) {
            console.error('Fecha inválida para ahorro:', saving);
            continue;
          }

          const savingData = {
            goal_name: saving.goalName || saving.goal_name,
            target_amount: parseFloat(saving.targetAmount || saving.target_amount),
            current_amount: parseFloat(saving.currentAmount || saving.current_amount || 0),
            target_date: targetDate.toISOString().split('T')[0]
          };

          const syncedSaving = await savingsService.create(savingData);
          setSavings(prev => 
            prev.map(sav => 
              sav.id === saving.id ? { ...syncedSaving, synced: true } : sav
            )
          );
        } catch (error) {
          console.error('Error sincronizando ahorro:', error);
        }
      }

      // Actualizar localStorage
      localStorage.setItem('expenses', JSON.stringify(expenses));
      localStorage.setItem('savings', JSON.stringify(savings));
    } catch (error) {
      console.error('Error en sincronización:', error);
    }
  };

  // Intentar sincronizar datos pendientes cuando haya conexión
  useEffect(() => {
    const handleOnline = () => {
      const hasPendingData = [...expenses, ...savings].some(item => !item.synced);
      if (hasPendingData) {
        syncPendingData();
      }
    };

    window.addEventListener('online', handleOnline);
    return () => window.removeEventListener('online', handleOnline);
  }, [expenses, savings]);

  // Manejador para agregar nuevo gasto
  const handleAddExpense = async (expenseData) => {
    try {
      const newExpense = {
        ...expenseData,
        id: Date.now().toString(),
        date: new Date(expenseData.date).toISOString().split('T')[0],
        synced: false
      };

      try {
        const syncedExpense = await expensesService.create(newExpense);
        setExpenses(prev => [...prev, { ...syncedExpense, synced: true }]);
      } catch (error) {
        console.error('Error guardando gasto en servidor:', error);
        setExpenses(prev => [...prev, newExpense]);
      }

      localStorage.setItem('expenses', JSON.stringify(expenses));
    } catch (error) {
      console.error('Error agregando gasto:', error);
    }
  };

  // Manejador para agregar nueva meta de ahorro
  const handleAddSavingsGoal = async (savingsData) => {
    try {
      const targetDate = new Date(savingsData.targetDate);
      if (isNaN(targetDate.getTime())) {
        throw new Error('Fecha inválida');
      }

      const newSavingsGoal = {
        ...savingsData,
        id: Date.now().toString(),
        current_amount: 0,
        target_date: targetDate.toISOString().split('T')[0],
        synced: false
      };

      try {
        const syncedSaving = await savingsService.create({
          goal_name: newSavingsGoal.goalName,
          target_amount: parseFloat(newSavingsGoal.targetAmount),
          current_amount: 0,
          target_date: newSavingsGoal.target_date
        });

        setSavings(prev => [...prev, { ...syncedSaving, synced: true }]);
      } catch (error) {
        console.error('Error guardando ahorro en servidor:', error);
        setSavings(prev => [...prev, newSavingsGoal]);
      }

      localStorage.setItem('savings', JSON.stringify(savings));
    } catch (error) {
      console.error('Error agregando meta de ahorro:', error);
    }
  };

  // Definición de secciones
  const sections = useMemo(() => [
    { 
      id: 'overview', 
      icon: Icons.Home, 
      title: 'Resumen', 
      color: 'bg-gradient-to-r from-blue-500 to-teal-400',
      component: () => <OverviewPage expenses={expenses} savings={savings} />
    },
    { 
      id: 'savings', 
      icon: Icons.Savings, 
      title: 'Ahorros', 
      color: 'bg-gradient-to-r from-green-400 to-emerald-500',
      component: () => <SavingsGoalForm savings={savings} onAddGoal={handleAddSavingsGoal} />
    },
    { 
      id: 'expenses', 
      icon: Icons.Expenses, 
      title: 'Gastos', 
      color: 'bg-gradient-to-r from-yellow-400 to-orange-500',
      component: () => <ExpenseForm expenses={expenses} onAddExpense={handleAddExpense} />
    },
    { 
      id: 'analysis', 
      icon: Icons.Analysis, 
      title: 'Análisis', 
      color: 'bg-gradient-to-r from-purple-500 to-indigo-600',
      component: () => <AIFinancialAnalysis expenses={expenses} savings={savings} />
    },
    { 
      id: 'settings', 
      icon: Icons.Settings, 
      title: 'Config', 
      color: 'bg-gradient-to-r from-gray-400 to-gray-600',
      component: () => <SettingsPage />
    }
  ], [expenses, savings, handleAddExpense, handleAddSavingsGoal]);

  return (
    <div className="flex h-screen bg-gray-50">
      {/* Barra lateral */}
      <div className="w-24 bg-white shadow-lg p-4 flex flex-col space-y-4">
        {sections.map((section) => (
          <button
            key={section.id}
            onClick={() => setActiveSection(section.id)}
            className={`
              p-3 rounded-xl transition-all duration-300 ease-in-out transform
              hover:scale-110
              hover:shadow-xl focus:outline-none group
              ${activeSection === section.id 
                ? section.color + ' text-white' 
                : 'bg-gray-100 text-gray-600 hover:bg-gray-200'}
            `}
          >
            <section.icon className="w-6 h-6 mx-auto" />
            <span className="text-xs block text-center mt-1 opacity-75">
              {section.title}
            </span>
          </button>
        ))}
      </div>

      {/* Área principal de contenido */}
      <div className="flex-1 p-8 overflow-y-auto bg-gray-50">
        {error && (
          <div className="mb-4 p-4 bg-yellow-100 border-l-4 border-yellow-500 text-yellow-700">
            <p>{error}</p>
          </div>
        )}

        {isLoading ? (
          <div className="flex items-center justify-center h-full">
            <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-500"></div>
          </div>
        ) : (
          sections.find(section => section.id === activeSection)?.component()
        )}
      </div>
    </div>
  );
};

// Componentes auxiliares
const SettingsPage = () => (
  <div className="space-y-8">
    <h1 className="text-4xl font-bold text-gray-800 mb-6">Configuraciones</h1>
    <div className="bg-white rounded-2xl shadow-lg p-6">
      <h2 className="text-2xl font-semibold mb-4">Preferencias</h2>
      <div className="space-y-4">
        <p>Próximamente más configuraciones</p>
      </div>
    </div>
  </div>
);

const OverviewPage = ({ expenses = [], savings = [] }) => {
  const totalSavings = useMemo(() => 
    savings.reduce((acc, goal) => acc + parseFloat(goal.currentAmount || goal.current_amount || 0), 0),
    [savings]
  );

  const monthlyExpenses = useMemo(() => {
    return expenses
      .filter(expense => {
        try {
          const expenseDate = new Date(expense.date);
          const currentDate = new Date();
          return expenseDate.getMonth() === currentDate.getMonth() &&
                 expenseDate.getFullYear() === currentDate.getFullYear();
        } catch (error) {
          return false;
        }
      })
      .reduce((acc, expense) => acc + parseFloat(expense.amount || 0), 0);
  }, [expenses]);

  return (
    <div className="space-y-8">
      <h1 className="text-4xl font-bold text-gray-800 mb-6">Resumen Económico</h1>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        <div className="bg-gradient-to-r from-blue-500 to-teal-400 text-white p-6 rounded-2xl shadow-lg">
          <h3 className="text-sm opacity-75 mb-2">Ahorros Totales</h3>
          <p className="text-2xl font-bold">${totalSavings.toLocaleString()}</p>
        </div>
        <div className="bg-gradient-to-r from-yellow-400 to-orange-500 text-white p-6 rounded-2xl shadow-lg">
          <h3 className="text-sm opacity-75 mb-2">Gastos Mensuales</h3>
          <p className="text-2xl font-bold">${monthlyExpenses.toLocaleString()}</p>
        </div>
        <div className="bg-gradient-to-r from-green-400 to-emerald-500 text-white p-6 rounded-2xl shadow-lg">
          <h3 className="text-sm opacity-75 mb-2">Metas de Ahorro</h3>
          <p className="text-2xl font-bold">{savings.length} Metas</p>
        </div>
      </div>

      <div className="grid grid-cols-1 xl:grid-cols-2 gap-6">
        <ExpenseDistribution expenses={expenses} />

        <div className="bg-white rounded-2xl shadow-lg p-6">
          <h2 className="text-2xl font-semibold mb-4">Metas de Ahorro Activas</h2>
          <div className="space-y-4">
            {savings.length > 0 ? (
              savings.map((goal) => (
                <div key={goal.id || Date.now()} className="border rounded-lg p-4">
                  <h3 className="font-semibold">{goal.goalName || goal.goal_name}</h3>
                  <div className="mt-2">
                    <div className="flex justify-between text-sm text-gray-600">
                      <span>
                        Progreso: ${(parseFloat(goal.currentAmount || goal.current_amount) || 0).toLocaleString()} / 
                        ${(parseFloat(goal.targetAmount || goal.target_amount) || 0).toLocaleString()}
                      </span>
                      <span>
                        {Math.round(((parseFloat(goal.currentAmount || goal.current_amount) || 0) / 
                        (parseFloat(goal.targetAmount || goal.target_amount) || 1)) * 100)}%
                      </span>
                    </div>
                    <div className="w-full bg-gray-200 rounded-full h-2.5 mt-1">
                      <div
                        className="bg-blue-600 h-2.5 rounded-full"
                        style={{ 
                          width: `${Math.min(((parseFloat(goal.currentAmount || goal.current_amount) || 0) / 
                          (parseFloat(goal.targetAmount || goal.target_amount) || 1)) * 100, 100)}%` 
                        }}
                      />
                    </div>
                  </div>
                </div>
              ))
            ) : (
              <p className="text-gray-500 text-center py-4">
                No hay metas de ahorro activas
              </p>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default App;
