import React, { useState } from 'react';

const SavingsGoalForm = ({ onAddGoal }) => {
  const [goalData, setGoalData] = useState({
    goalName: '',
    targetAmount: '',
    currentAmount: '',
    targetDate: ''
  });
  const [error, setError] = useState(null);

  const handleChange = (e) => {
    setGoalData({ ...goalData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);

    // Validaciones
    if (!goalData.goalName.trim()) {
      setError('El nombre del objetivo es obligatorio');
      return;
    }

    const targetAmount = parseFloat(goalData.targetAmount);
    const currentAmount = goalData.currentAmount ? parseFloat(goalData.currentAmount) : 0;

    if (isNaN(targetAmount) || targetAmount <= 0) {
      setError('El monto objetivo debe ser un número positivo');
      return;
    }

    if (isNaN(currentAmount) || currentAmount < 0) {
      setError('El monto actual debe ser un número positivo o 0');
      return;
    }

    if (!goalData.targetDate) {
      setError('La fecha objetivo es obligatoria');
      return;
    }

    const savingData = {
      goalName: goalData.goalName,
      targetAmount,
      currentAmount,
      targetDate: goalData.targetDate // Mantener el formato de fecha correcto
    };

    try {
      const response = await fetch('http://localhost:3001/api/savings', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(savingData)
      });

      if (!response.ok) {
        throw new Error('Error en la creación del ahorro');
      }

      const newGoal = await response.json();
      onAddGoal(newGoal);
      setGoalData({ goalName: '', targetAmount: '', currentAmount: '', targetDate: '' });
    } catch (error) {
      setError(error.message);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <input type="text" name="goalName" placeholder="Nombre del objetivo" value={goalData.goalName} onChange={handleChange} required />
      <input type="number" name="targetAmount" placeholder="Monto objetivo" value={goalData.targetAmount} onChange={handleChange} required />
      <input type="number" name="currentAmount" placeholder="Monto actual" value={goalData.currentAmount} onChange={handleChange} />
      <input type="date" name="targetDate" value={goalData.targetDate} onChange={handleChange} required />
      <button type="submit">Guardar</button>
    </form>
  );
};

export default SavingsGoalForm;
